/**
* File Name   placeholders.min.js
* Purpose     To check if placeholders are supported and add labels to inputs when not supported.
*/
function isPlaceholderSupported(){var e=document.createElement("input");return"placeholder"in e}function addLabel(e){var t=e.attr("placeholder");if(!t||e.hasClass("labelled"))return false;e.before('<label class="ie-placeholder">'+t+"</label");e.addClass("labelled")}$(document).ready(function(){var e=isPlaceholderSupported();if(!e){$("input").each(function(){addLabel($(this))})}})